import styled from 'styled-components';

export const StyledBurger = styled.button`
  top: 5%;
  left: 2rem;
  margin-right: 2em;
  margin-top: 1.5em;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 2rem;
  height: 2rem;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  z-index: 1000;

  &:focus {
    outline: none;
  }

  div {
    width: 2rem;
    height: 0.25rem;
    background: #312A2D; 
    border-radius: 10px;
    transition: all 0.3s linear;
    position: relative;
    transform-origin: 1px;
    z-index: 1000;

    :first-child {
      background-color: ${({open}) => open ? 'red' : 'green'}
    }
  }
`;
