import React, { Component } from 'react';
import {Accordion, Card, Button} from 'react-bootstrap';

class AccordionCard extends Component {

	constructor(props) {
		super(props);
		this.state = {
			open: false
		}
	}

	onClick() {
		this.setState({open: !this.state.open});
	}

	render() {
		let iconClass =  "fa-plus";
		if (this.state.open) {
			iconClass = "fa-minus";
		}
		return (
				<Card>
					<Card.Header>
						<Accordion.Toggle onClick={this.onClick.bind(this)} variant="link" eventKey={this.props.eventKey}>
							<span className="question">{this.props.title}</span>
							<span className={"fas " + iconClass}></span>
						</Accordion.Toggle>
					</Card.Header>
					<Accordion.Collapse eventKey={this.props.eventKey}>
						<Card.Body> {this.props.children} </Card.Body>
					</Accordion.Collapse>
				</Card>
		);
	}
}

export default AccordionCard;
