import React, {Component} from 'react';
import { connect } from 'react-redux';
import {Redirect} from "react-router-dom";
import queryString from 'query-string';
import { joinWaitlist } from "../../actions/waitlist";

class NeverlandHome extends Component {
	constructor(props) {
		super(props);
		this.state = {
			fIndex: 0,
			emailInput: '',
			emailError: "",
			isSubmitting: false ,
			inviter: ""

		}
		this.onClickWaitlist = this.onClickWaitlist.bind(this);
		this.onChangeInput= this.onChangeInput.bind(this);
	}

	componentDidMount() {
		if (this.props && this.props.location) {
			const values = queryString.parse(this.props.location.search);
			this.setState({
				inviter: values.invite
			});
		}
	}

	genFunction() {
		let functions = ["aging", "life", "stress", "sleep"];
		return (
				<span>{functions[this.state.fIndex]}</span>
		);
	}

	componentDidUpdate(prevProps, prevState) {
		let waitlistUser = this.props.waitlist.waitlistUser;
		if (waitlistUser && waitlistUser.referralCode) {
			this.setState({
				redirect: true
			})
		}
	}

	validateEmail(email) {
    	var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    	return re.test(String(email).toLowerCase());
	}

	onClickWaitlist() {
		//validate email
		if (this.validateEmail(this.state.emailInput)) {
			this.props.joinWaitlist(this.state.emailInput, this.state.inviter);
			this.setState({
				"emailError": "",
				isSubmitting: true
			});
		} else {
			this.setState({"emailError": "Please enter valid email"})
		}
		//window.location = "https://docs.google.com/forms/d/e/1FAIpQLSeZcRVCsn-_cOXdcMyjEfR7PQ9N536zi0NGdVZRbcfE4KUCpg/viewform"
	}

	onChangeInput(event) {
		this.setState({
			emailInput: event.target.value 
		});
	}

	render() {
		let message = "";
		if (this.props.waitlist) {
			message = this.props.waitlist.message;
		}
		if (this.state.redirect) {
			return (<Redirect to="/immunity/blue/waitlist" />);
		}
		if (this.state.isSubmitting) {
			return <div>Joining the waitlist...</div>;
		}
		return (
			<div> 	
				<div className="row-nm neverland-header">
					<div className="neverland-header-container">
							<h1><span className="swash">G</span>row an oasis that <span className="ss-n">n</span>e<span className="ss-v">v</span>e<span className="swash">r</span> sta<span className="ss-n">n</span>ds still</h1>
							<h2><span className="swash">D</span>elight in growi<span className="ss-n">n</span>g with our plant packs.</h2>
							<input onChange={this.onChangeInput} className="neverland-input" value={this.state.emailInput}/>
							<button onClick={this.onClickWaitlist} className="neverland-button"> JOIN THE WAITLIST </button><br/>
							<span>{this.state.emailError}</span>
					</div>
				</div>
				<div className="row-nm">
					<h2 className="h2-cognace" style={{margin: 'auto'}}> How it works </h2>
					<div className="row-nm">
						<div className="col-sm-4">
							<img src=""/>
							<span>Pick</span>
							<p>We're mint to be. Choose a plant you'd like to grow and we'll send you a pack with everything you need delivered to your door.</p>
						</div>
						<div className="col-sm-4">
							<img src=""/>
							<span>Plant</span>
							<p>Unbox your plant pack and follow our easy breezy instructions for planting your new friend. Make sure to pot it like it's hot.</p>
						</div>
						<div className="col-sm-4">
							<img src=""/>
							<span>Grow</span>
							<p>Romaine calm, we're here to kelp. Text us at 1800PartyThyme for a good time and any questions you have. We'll send text reminders, plant puns and tips to keep your plant happy. We're rooting for you.</p>
						</div>
					</div>
				</div>
				<div>
					<img src="image divider" />
				</div>
				<div className="row-nm">
					<div className="col-sm-6 romaine-calm-container">
						<h2 className="h2-cognace">Romaine calm, we're here to kelp.</h2>
						<p>Never kill a plant again with our plant hotline. Reminders to water, fertilize, and repot your plants. Text us with any questions. Keep track of your plants in one place.</p>
					</div>
					<div className="col-sm-6"><img style={{width: '80%'}}src="/images/neverland_textline.png"/></div>
				</div>
				<div>
					<h2 className="h2-cognace" style={{textAlign: 'center'}}>Our Plant Packs</h2>
					<div>
						<div>Image</div>
						<div>
							<h3>Monty</h3>
							<p>She's a tough one. a beaut, instagrammable. Will make drama in the livin room. </p>
							<span>pet-friendly</span><span>low-light</span>
							<p>One pack contains:</p>
							<div>
								<p>1x pot</p>
								<p>1x soil</p>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

const mapStateToProps = state => {
	return {
		waitlist: state.waitlist
	}
}

export default connect(mapStateToProps, {joinWaitlist})(NeverlandHome);
